import axios from "axios"
import React, {Component} from 'react'
import Plot from 'react-plotly.js';

export default class StockList extends Component{
    constructor(){
        super();
        this.state = {
            stocks:""
        }
    };

    componentDidMount(){
        axios.get('http://localhost:5000/get_all_stocks')
        .then( res => {
            this.setState({stocks:res.data})
        } )
    }
    
    create_body(){

    }
    
    render(){
        const str = this.state.stocks
        const stockName = ""
        const datesArr = []
        const valuesArr = []
        var arr = str.split(",")
        var i = 0
        var isDate = true
        for (i=0;i<arr.length;i++){
            if (i == 0){
                stockName = arr[0]
            }
            else {
                if (arr[i] === "NowValues"){
                    isDate = false
                }
                else {
                    if (isDate == true){
                        datesArr.push(arr[i])
                    }
                    else if (isDate == false){
                        valuesArr.push(arr[i])
                    }
                }

            }
        }
        return(
          <div>
              <Plot
                data={[
                {
                    x: datesArr,
                    y: valuesArr,
                    type: 'line'
                }
                ]}
                layout={ {width: 320, height: 240, title: 'A Fancy Plot'} }
              />
          </div>  
        )
    }

}
