import React,{Component} from 'react'
import axios from "axios"
import { Button,Modal, ModalBody } from "react-bootstrap";

export default class ReplaceStock extends Component{
    constructor(props){
        super(props);
        this.state = {
            stockName:""
        }
        const [show, setShow] = useState(false);
        const handleClose = () => setShow(false);
        const handleShow = () => setShow(true);
        this.stockName = this.stockName.bind(this);
        this.toggle = this.toggle.bind(this);
    }

    //Toggles the modal window between Open and Closed
    toggle(){
        this.setState( state => ({ modal:!state.modal }) )
    }

    stockName(e){
        this.setState({stockName:e.target.value})
    }

    onSubmit(e){
        e.preventDefault();
        const newStockName = this.state.stockName;
        
        //Closes window
        this.handleClose();
        axios.post('http://localhost:5000/replace_stock',{"stockName":newStockName,"previousStockName":this.props.stockName})
        //Refresh page
        window.location.reload()
    }

    render(){
        return (
            <div>
                <button className="material-icons" style={{boxShadow:"none",fontSize:"1.5em",position:"absolute",left:"9%",top:"3%"}} onClick={handleShow}>
                    edit
                </button>
                
                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Replace Stock</Modal.Title>
                    </Modal.Header>
                    <form onSubmit={this.onSubmit}>
                        <Modal.Body>
                            <input type="text"
                                required
                                className="form-control"
                                value={this.state.stockName}
                                onChange={this.stockName}
                            />
                        </Modal.Body>
                        <Modal.Footer>
                            <Button color="primary" type="submit">Replace</Button>
                            <Button color="danger" onClick={this.toggle}>Cancel</Button>
                        </Modal.Footer>
                    </form>
                </Modal>
            </div>
        )
    }

}
