import React,{Component} from 'react'
import axios from "axios"
import { Button,Modal,ModalBody,ModalHeader,ModalFooter } from "reactstrap";

export default class ReplaceStock extends Component{
    constructor(props){
        super(props);
        this.state = {
            modal:false,
            backdrop:true,
            fade:false,
            stockName:""
        }
        this.stockName = this.stockName.bind(this);
        this.toggle = this.toggle.bind(this);
    }

    //Toggles the modal window between Open and Closed
    toggle(){
        this.setState( state => ({ modal: !state.modal }) )
    }

    stockName(e){
        this.setState({stockName:e.target.value})
    }

    onSubmit(e){
        e.preventDefault();
        const newStockName = this.state.stockName;
        
        //Closes window
        this.toggle();
        axios.post('http://localhost:5000/replace_stock',{"stockName":newStockName,"previousStockName":this.props.stockName})
        //Refresh page
        window.location.reload()
    }

    render(){
        return (
            <div>
                <button className="material-icons" style={{boxShadow:"none",fontSize:"1.5em",position:"absolute",left:"9%",top:"3%"}} onClick={this.toggle}>
                    edit
                </button>
                <Modal isOpen={this.state.modal} toggle={this.toggle} fade={this.state.fade } backdrop={this.state.backdrop}>
                    <ModalHeader toggle={this.toggle}>Replace Stock</ModalHeader>
                    <form onSubmit={this.onSubmit}>
                        <ModalBody>
                            <input type="text"
                                required
                                className="form-control"
                                value={this.state.stockName}
                                onChange={this.stockName}
                            />
                        </ModalBody>
                        <ModalFooter>
                            <Button color="primary" type="submit">Replace</Button>
                            <Button color="black" onClick={this.toggle}>Cancel</Button>
                        </ModalFooter>
                    </form>
                </Modal>
            </div>
        )
    }

}
