import axios from "axios"
import React, {Component} from 'react'
import { Row,Accordion,Card } from "react-bootstrap";
import Plot from 'react-plotly.js';

export default class StockList extends Component{
    constructor(){
        super();
        this.state = {
            stocks:[]
        }
        this.create_plot = this.create_plot.bind(this)
    };

    componentDidMount(){
        axios.get('http://localhost:5000/get_all_stocks')
        .then( res => {
            var stocks_arr = []
            Object.keys(res.data).forEach(k => {
                stocks_arr.push(res.data[k])
            })
            this.setState({stocks:stocks_arr})
        } )
    }
    
    create_plot(stock){
        var str = String(stock)
        var stockName = ""
        var datesArr = []
        var valuesArr = []
        var arr = str.split(",")
        var i = 0
        var isDate = true
        for (i=0;i<arr.length;i++){
            
            if (i == 0){ stockName = arr[0] }
            else if (arr[i] === "NowValues"){ isDate = false }   
            else if (isDate == true) { datesArr.push(arr[i]) }
            else if (isDate == false){ valuesArr.push(arr[i]) }
        }
        
        var i = 0
        datesArr.forEach(date => { 
            var parts = date.split('/');
            var newDate = new Date(parts[0],parts[1] - 1,parts[2]);
            datesArr[i] = newDate;
            i++;
        });
        return(
            <div>
                <Accordion defaultActiveKey="0">
                    <Card>
                        <Card.Header>
                        <Accordion.Toggle as={Button} variant="link" eventKey="0">
                            {stockName}
                        </Accordion.Toggle>
                        </Card.Header>
                        <Accordion.Collapse eventKey="0">
                        <Card.Body><Plot
                                data={[
                                {
                                    x: datesArr,
                                    y: valuesArr,
                                    fill: "tonexty",
                                    type: 'line'
                                }
                                ]}
                                layout={ {plot_bgcolor:'#FAF0E6', width:500,height:500, title: stockName, xaxis:{title:"Dates"}, yaxis:{title:"Values"}} }/>
                        </Card.Body>
                        </Accordion.Collapse>
                    </Card>
                </Accordion>
                
            </div>
        )
    }
    
    render(){
        var stock_list = this.state.stocks
        var body = []
        stock_list.forEach(stock => {
            body.push(this.create_plot(stock))
        });
        return (<div><Row>{body}</Row></div>)
    }

}
