import React,{Component} from 'react'
import axios from "axios"
import { Button,Modal,ModalBody,ModalHeader } from "reactstrap";

export default class ReplaceStock extends Component{
    constructor(props){
        super(props);
        this.state = {
            modal:false,
            backdrop:true,
            stockName:""
        }
        this.stockName = this.stockName.bind(this);
        this.toggle = this.toggle.bind(this);
    }

    //Toggles the modal window between Open and Closed
    toggle(){
        this.setState( state => ({ modal: !state.modal }) )
    }

    stockName(e){
        this.setState({stockName:e.target.value})
    }

    onSubmit(e){
        e.preventDefault();
        const newStockName = this.state.stockName;
        
        //Closes window
        this.toggle();
        axios.post('http://localhost:5000/replace_stock',{"stockName":newStockName,"previousStockName":this.props.stockName})
        //Refresh page
        window.location.reload()
    }

    render(){
        return (
            <div>
                <Button>
                    <i className="material-icons" style={{fontSize:"1.5em",position:"absolute",left:"9%",top:"3%"}}>edit</i>
                </Button>
                <Modal isOpen={this.state.modal} toggle={this.toggle} backdrop={this.state.backdrop}>
                    <ModalHeader toggle={this.toggle}>Replace Stock</ModalHeader>
                    <ModalBody>
                        <input type="text"
                               required
                               className="form-control"
                               value={this.state.stockName}
                               onChange={this.stockName}
                        />
                    </ModalBody>
                </Modal>
            </div>
        )
    }

}
